<?php 	$url_tebak = base_url('index.php/tebak'); 	$url_ajax = base_url('index.php/user/login/ajax');?><div class="grid_8">	<form id="form_login">	<input type="hidden" id="url_tebak" value="<?php echo $url_tebak; ?>">	<input type="hidden" id="url_ajax" value="<?php echo $url_ajax; ?>">	<table style="width: 400px;">		<tr>			<td>				<input type="text" name="username" placeholder="Username">			</td>		</tr>		<tr>			<td>				<input type="password" name="password" placeholder="Password">			</td>		</tr>		<tr>			<td style="align: center; ">				<input type="submit" id="submit_login" class="button" style="width: 100%" value="Login" />			</td>		</tr>	</table>	</form></div><div class="grid_8 judul">	<h1>Login</h1></div><script>$('#form_login').submit(function(event) {	var url = $('#form_login #url_ajax').val();	/* stop form from submitting normally */	event.preventDefault();		/* get some values from elements on the page: */	var data_post = $( this ).serialize();		//disabled tombolnya	$("#submit_login").prop('disabled', true);	$("#submit_login").addClass('buttonDisabled');		/* Send the data using post */	var posting = $.post( url, data_post, function(data){		if(data.sukses == true) {			var url_tebak = $('#form_login #url_tebak').val();			//redirect ke halaman tebak			window.location.replace(url_tebak);		} else {			//enable lagi tombolnya			$("#submit_login").prop('disabled', false);			$("#submit_login").removeClass('buttonDisabled');			alert(data.error);		}	}, 'json');  });</script>