<?php 	$url_login = base_url('index.php/user/login'); 	$url_ajax = base_url('index.php/user/register/ajax');?><div class="grid_8">	<form id="form_register">	<input type="hidden" id="url_login" value="<?php echo $url_login; ?>">	<input type="hidden" id="url_ajax" value="<?php echo $url_ajax; ?>">	<table style="width: 400px;">		<tr>			<td>				<input type="text" name="username" placeholder="Masukkan Username">			</td>		</tr>		<tr>			<td>				<input type="email" name="email" placeholder="Masukkan Email">			</td>		</tr>		<tr>			<td>				<input type="password" name="password" placeholder="Masukkan Password">			</td>		</tr>		<tr>			<td>				<input type="password" name="re_password" placeholder="Ketik Ulang Password">			</td>		</tr>		<tr>			<td style="align: center; ">				<input type="submit" id="submit_register" class="button" style="width: 100%" value="Register" />			</td>		</tr>	</table>	</form></div><div class="grid_8 judul">	<h1>Register</h1></div><script>$('#form_register').submit(function(event) {	var url = $('#form_register #url_ajax').val();	/* stop form from submitting normally */	event.preventDefault();		/* get some values from elements on the page: */	var data_post = $( this ).serialize();		//disabled tombolnya	$("#submit_register").prop('disabled', true);	$("#submit_register").addClass('buttonDisabled');		/* Send the data using post */	var posting = $.post( url, data_post, function(data){		if(data.sukses == true) {			var url_login = $('#form_register #url_login').val();			alert('Berhasil Register. Sekarang anda bisa login.');			console.log('Berhasil register');			//redirect			window.location.replace(url_login);		} else {			//enable lagi tombolnya			$("#submit_register").prop('disabled', false);			$("#submit_register").removeClass('buttonDisabled');			alert(data.error);		}	}, 'json');  });</script>